CREATE TABLE IF NOT EXISTS courses (id int(10) UNSIGNED NOT NULL AUTO_INCREMENT,title varchar(150) COLLATE utf8_unicode_ci NOT NULL,description varchar(255) COLLATE utf8_unicode_ci NOT NULL,cu int(10) COLLATE utf8_unicode_ci NOT NULL,passmark int(10) COLLATE utf8_unicode_ci NOT NULL,classId int(10) COLLATE utf8_unicode_ci NOT NULL,created_at timestamp NULL DEFAULT current_timestamp(),updated_at timestamp NULL DEFAULT current_timestamp(), PRIMARY KEY (id)) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;